def ApproximatePatternMatching(Text, Pattern, d):
	k = len(Pattern)
	L = len(Text)
	start_idx = []
	for i in range(L - k + 1):
		if HammingDistance(Text[i:i+k], Pattern) <= d:
			start_idx.append(i)
	return start_idx

def HammingDistance(p, q):
	mm = [p[i] != q[i] for i in range(len(p))]
	return sum(mm)


Text = ''
Pattern = 'CTTGGCCTATGC'
