def ApproximatePatternCount(Text, Pattern, d):
	count = 0 # initialize count variable
	k = len(Pattern)
	L = len(Text)
	for i in range(L - k + 1):
		if HammingDistance(Text[i:i+k], Pattern) <= d:
			count += 1
	return count


